import React, { useState } from 'react';

export default function App() {
  const [username, setUsername] = useState('');
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const fetchUser = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`https://api.github.com/users/${username}`);
      if (!response.ok) throw new Error('Perfil não encontrado!');
      const data = await response.json();
      setUserData(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Estilo minimalista baseado na sua imagem
  const styles = {
    container: {
      fontFamily: "'Courier New', monospace",
      maxWidth: '600px',
      margin: '20px auto',
      padding: '10px',
      backgroundColor: '#f0f0f0',
      color: '#333',
      lineHeight: '1.4'
    },
    header: {
      fontWeight: 'bold',
      marginBottom: '15px',
      fontSize: '18px'
    },
    input: {
      fontFamily: "'Courier New', monospace",
      padding: '5px',
      marginRight: '5px',
      border: '1px solid #ccc',
      backgroundColor: '#fff'
    },
    button: {
      fontFamily: "'Courier New', monospace",
      padding: '5px 10px',
      backgroundColor: '#ddd',
      border: '1px solid #999',
      cursor: 'pointer'
    },
    profile: {
      margin: '15px 0',
      padding: '10px',
      borderLeft: '3px solid #999'
    },
    profileName: {
      fontWeight: 'bold'
    },
    error: {
      color: '#c00'
    },
    section: {
      margin: '15px 0',
      padding: '10px',
      borderTop: '1px dashed #999'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>Perfil GitHub</div>

      <div>
        <input
          type="text"
          placeholder="Digite o nome do usuário"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={styles.input}
          onKeyPress={(e) => e.key === 'Enter' && fetchUser()}
        />
        <button onClick={fetchUser} style={styles.button}>
          Buscar
        </button>
      </div>

      {loading && <div>Carregando...</div>}
      {error && <div style={styles.error}>{error}</div>}

      {userData && (
        <div style={styles.profile}>
          <div style={styles.profileName}>{userData.name || userData.login}</div>
          <div>@{userData.login}</div>
          {userData.bio && <div>{userData.bio}</div>}
        </div>
      )}

      <div style={styles.section}>
        <div style={styles.profileName}>Local Heroes</div>
        <div>Downloads local Heroes with a browser version of the Android version. This is a browser version that can be used to create local Heroes for more than 20 years.</div>
      </div>

      <div style={styles.section}>
        <div style={styles.profileName}>Puffer inside ab Ghub</div>
        <div>Nashim perfil @ GigabitHub can see sense de models. Photo download</div>
      </div>

      <div style={styles.section}>
        <div>Quem per fi no responde que es bonito o usar?</div>
        <div>Todo empírico.</div>
      </div>
    </div>
  );
}
